	can_update BOOLEAN,
	page_id       BIGINT	NOT NULL	CONSTRAINT fk4h3hdiy81a4qjyhl94u35wbjt REFERENCES pages,
	profile_id    BIGINT 	NOT NULL	CONSTRAINT fk83dj0mj2va9fcwpgw1tta289f REFERENCES profile,
	CONSTRAINT ukjyvkiitug18b0ig8e7aoias82
	UNIQUE (profile_id, page_id)
);

CREATE TABLE IF NOT EXISTS state (
	id      BIGINT       NOT NULL CONSTRAINT state_pkey PRIMARY KEY,
	uf_abbr VARCHAR(255) NOT NULL CONSTRAINT uk_11llv031ayg9lcqp0k8jl3nd7 UNIQUE,
