CREATE TABLE IF NOT EXISTS city (
	id          BIGINT       NOT NULL CONSTRAINT city_pkey PRIMARY KEY,
	name        VARCHAR(255) NOT NULL,
	state_id_fk BIGINT       NOT NULL CONSTRAINT fk5mkf8607s48qf9pi7fjgvhy76 REFERENCES state
);